import React, { Component } from 'react';
import { FaPlay } from 'react-icons/fa';
import Observer from 'react-intersection-observer';
import { NavConsumer } from './layout';
import YouTube from 'react-youtube';

class Banner extends Component {
  constructor(props) {
    super(props);

    this.state = {
      playerReady: false,
      playing: false,
      player: false,
      canAuto: false,
      playinline: 1
    }

    this._onReady = this._onReady.bind(this);
    this._onEnd = this._onEnd.bind(this);
    this._onPlay = this._onPlay.bind(this);
    this._onPause = this._onPause.bind(this);
    this.onPlayClick = this.onPlayClick.bind(this);
    //this.onPlayClickNoAuto = this.onPlayClickNoAuto.bind(this);
    this.exitFullScreen = this.exitFullScreen.bind(this);
    
  }

  componentDidMount() {
    //this.detect_autoplay();
    document.addEventListener('webkitfullscreenchange', this.fullScreenExit.bind(this), false);
    document.addEventListener('fullscreenchange', this.fullScreenExit.bind(this), false);
    document.addEventListener('mozfullscreenchange', this.fullScreenExit.bind(this), false);
    document.addEventListener('MSFullscreenChange', this.fullScreenExit.bind(this), false);
  }

  detect_autoplay() {
    let auto_content = document.createElement('video');

    let mp4 = document.createElement('source');
    mp4.src = "data:video/mp4;base64,";

    let webm = document.createElement('source');
    webm.src = "data:video/webm;base64,";

    auto_content.appendChild(webm);
    auto_content.appendChild(mp4);

    auto_content.id = "base64_test_video";
    auto_content.autoplay = true;
    auto_content.muted = true;
    auto_content.style.position = "fixed";
    auto_content.style.left = "5000px";

    document.getElementsByTagName("body")[0].appendChild(auto_content);
    
    let base64_test_video = document.getElementById("base64_test_video");
    

    setTimeout(() => {
      let promise = base64_test_video.play()

      if(promise !== undefined) {
        promise.then(_ => {

        }).catch(error => {
          console.log(error);
        });
      }

      if(!base64_test_video.paused) {
        this.setState({ canAuto: true });
      }
      
      document.getElementsByTagName("body")[0].removeChild(auto_content);
    }, 100)
  }

  _onReady(e) {

    if(this.state.playinline === 0) {
      e.target.unMute();
    } else {
      e.target.mute();
      
    }

    this.setState({
      playerReady: true,
      playing: false,
      player: e.target
    });

    e.target.playVideo();
  }

  _onEnd(e) {
      console.log("On end!");
      e.target.playVideo();
    
  }

  _onPlay(e) {
    this.setState({ playing: true })
  }

  _onPause(e) {
    if(this.state.playinline === 0) {
      this.setState({ playinline: 1 });
    }
  }

  exitFullScreen() {
    console.log('Exit Full Screen!');
  }

  fullScreenExit() {
    if(this.state.player.isMuted()) {
      this.state.player.unMute();
    } else {
      this.state.player.mute();
    }
  }

  onPlayClick() {  
    if(this.state.player) {
      let iFrame = document.querySelector('.video-iframe');
      let requestFullScreen = iFrame.requestFullScreen || iFrame.mozRequestFullScreen || iFrame.webkitRequestFullScreen;
      
      if(requestFullScreen) {
        
        requestFullScreen.bind(iFrame)();
        //this.state.player.unMute();

        
      } else {
        this.setState({ playinline: 0 })
          //this.state.player.playVideo();
          //this.state.player.unMute();
      
      }

    }
  }

  onPlayClickNoAuto() {
    if(this.state.player) {
      this.state.player.unMute();
      this.state.player.playVideo();
      this.setState({ playing: true });
    }
  }

  render() {
    const videoOptions = {
      playerVars: {
        autoplay: 1,
        controls: 0,
        rel: 0,
        showinfo: 0,
        playsinline: this.state.playinline
      }
    }

    return (
    <NavConsumer>
      {({ trigger }) => (
          <Observer 
              id="banner"
              tag="section" 
              className={`major`}
              threshold={0}
              style={{ backgroundImage: !this.state.playing ? `url(https://img.youtube.com/vi/${this.props.videoId}/maxresdefault.jpg)` : 'none'}}
              onChange={(inView) => { trigger(!inView)}}>
              <div className="video-background">
                <div className="video-foreground">
                  <YouTube
                    videoId={this.props.videoId}
                    opts={videoOptions}
                    className="video-iframe"
                    onReady={this._onReady}
                    onEnd={this._onEnd}
                    onPlay={this._onPlay}
                    onPause={this._onPause}
                  />      
                </div>
              </div>     
              <div className="inner">
                  <div className="row">
                  <header className="major col-md-6">
                    <div className="box">
                      <h1>{this.props.title}</h1>
                    </div>
                  </header>
                  <div className="content col-md-6">
                      <p>{this.props.introText}</p>
                      {this.state.playing && 
                        <ul className="actions">
                            <li><button aria-label="Full Screen Video" onClick={this.onPlayClick} className="button"><FaPlay /></button></li>
                        </ul>
                      }
                      {/*this.state.playerReady && !this.state.playing && !this.state.canAuto &&
                        <ul className="actions">
                          <li><button aria-label="Full Screen Video" onClick={this.onPlayClickNoAuto} className="button"><FaPlay /></button></li>
                        </ul>
                      */}
                  </div>
                  </div>
              </div> 
          </Observer>
      )}
    </NavConsumer>
    );
  }
}

export default Banner;